// 상세페이지 - 리뷰 평점 계산
const getProdReviewPointByDetail = function(target='.review-avg', limit_page_size = 0){
    let review_points = [], promises = [], limit = 1, last_page = 1, last_page_temp;

	if ($('.wp-review-list').length == 0) return; // 리뷰 목록이 없다면 종료

	// 리뷰 마지막 페이지 번호
	const $page_el = $('.xans-product-reviewpaging a.last');
    if($page_el.length > 0){
        last_page_temp = getParamUrl('page_4','/'+$page_el.attr('href'));
		if (last_page_temp) last_page = parseInt(last_page_temp.replace(/[^0-9]/g,''));
    }

	const product_no = $('meta[property="product:productId"]').attr('content');

    let n = 1;
    while (n <= last_page){
        promises.push(new Promise(function (resolve, reject) {
            $.get('/_wp/module/product/review/dp.html?product_no='+product_no+'&page_4='+n, function(d){
                $(`<div>${d}</div>`).find('point').each(function(){
                    review_points.push(parseInt($(this).text()));
                }).promise().done(function(){
                    resolve();
                });
            })
        }));
        if (limit_page_size > 0) {
            if (n == limit_page_size) break;
        }
        n++;
    }

    Promise.all(promises).then(function () {
        if (review_points.length > 0) {
            const sum = review_points.reduce((a, b) => (a + b));
            const result = (sum / review_points.length).toFixed(1);
            $(target).empty().append(`<rate class="rating" style="width:${(result * 2) * 10}%"></rate>`);
			$(target).after(`<point>${result}</point>`);
        }
    });
}

$(function(){
	getProdReviewPointByDetail();
});
$(function(){
	// 구매하기 - 하단고정
	$(window).scroll(function(){
		scrollOptionFixed(this);
		detailMenuFixed(this);
	});

	const scrollOptionFixed = function(target){
		if (!target) target = document;
		if(($('.detailArea').offset().top + $('.detailArea').height()) < $(target).scrollTop()){
			if (!$('#optionG').hasClass('active')){
				$('.infoArea').css('height', $('.infoArea').height());
				$('#optionG').addClass('active');
				if ($('.infoArea .productSet').length > 0){
					$('.wp-opt-box .opt-left').append($('.infoArea .productSet'));
				}
                $('.naver-kakao-pay').appendTo($('.wp-opt-area'));
                $('.wp-opt-area').hide();
			}
		}else{
			if ($('#optionG').hasClass('active')){
				$('.wp-opt-area').show();
				$('.infoArea').css('height', 'auto');
				$('#optionG').removeClass('active');
				if ($('.infoArea .productSet').length > 0){
					$('.infoArea #optionG').after($('.infoArea .productSet'));
				}

                $('.naver-kakao-pay').appendTo($('.wp-opt-box3 .productAction'));
			}
		}
	}

	var selectedTabId = ''; //현재 활성화 된 탭 id
	function selectTab(id){
		if (selectedTabId == id) return;
		$('.ec-base-tab .menu-inner .menu li').removeClass('selected');
		$('.ec-base-tab .menu-inner .menu li a').each(function(){
			if ($(this).attr('href') == id){
				$(this).closest('li').addClass('selected');
				selectedTabId = id;
				return false;
			}
		});
	}

	$('.ec-base-tab .menu-inner a').click(function(e){
		e.preventDefault();
		var tid = $(this).attr('href');
        var margin = 100;
        if($('html').hasClass('res-mobile')) margin = 70;
		if (tid.includes('prdDetail')){
			margin = 0;
		}
        $(window).scrollTop($(tid).offset().top - margin);
        selectTab(tid);
	});

	// 탭 메뉴 상단 고정
	const detailMenuFixed = function(target){
		if (!target) target = document;
		if ($('.menu-inner').offset().top < $(target).scrollTop()){
			$('.menu-inner').addClass('fixed');
		}else{
			$('.menu-inner').removeClass('fixed');
		}

		// tab menu selection according to scroll
		var scrollTop = $(this).scrollTop();
		var fixedTabHeight = $('.ec-base-tab .menu-inner').height();
        var fixedScrollTop = scrollTop + fixedTabHeight; // 탭 고정 시 탭 기준으로 scrollTop 값 출력

        if ($('#prdDetail').offset().top < fixedScrollTop
            && $('#prdDetail').offset().top + $('#prdDetail').outerHeight() > fixedScrollTop){
            selectTab('#prdDetail');
        }else if ($('#prdReview').offset().top < fixedScrollTop
                  && $('#prdReview').offset().top + $('#prdReview').outerHeight() > fixedScrollTop){
            selectTab('#prdReview');
        }else if ($('#prdQnA').offset().top < fixedScrollTop
                  && $('#prdQnA').offset().top + $('#prdQnA').outerHeight() > fixedScrollTop){
            selectTab('#prdQnA');
        }else if ($('#prdInfo').offset().top < fixedScrollTop
                  && $('#prdInfo').offset().top + $('#prdInfo').outerHeight() > fixedScrollTop){
            selectTab('#prdInfo');
        }
	}

	scrollOptionFixed();
	detailMenuFixed();

	$('#optionG .btn-extend').on('click', function(e){
		e.preventDefault();

		$('#optionG.active').toggleClass('extend');
		$('#optionG.active .wp-opt-area').slideToggle(200);
	});

	// 상품명
	const prod_name =  $('#wpDetailInfo .product_name_css td > span').text();

	// 구매하기 - 하단고정 - 옵션확장
	if ($('#optionG tbody.xans-product-option').length > 0 || $('.infoArea .productSet').length > 0){
		$('.wp-opt-box2').append(`<div id="prodName">${prod_name}</div>`);
	}else{
		$('#optionG .btn-extend').remove();
	}

	// 추가구성상품 토글
	$('.additional .btn-toggle').click(function(){
		$(this).toggleClass('active');
		$('.additional .product').toggle();
	});

	// 옵션 선택 없이 구매 / 장바구니 버튼 클릭 시 옵션창 오픈
	$('.actionCart, .btnSubmit').click(function(){
		if (parseInt($('#totalPrice em').text().replace(/[^0-9]/g,''),10) <= 0){
			$('#optionG.active .btn-extend').trigger('click');
		}
	});
});
function removePagingArea(oTarget)
{
    if ($(oTarget).length < 1 && (oTarget != '#prdReview' || oTarget != '#prdQna')) return;

    if ($(oTarget).css('display') == 'block') {
        if (oTarget == '#prdReview') {
            var record = $('.xans-record-', '.xans-product-review').first();
            if (record.length < 1 || record.is(':not(:visible)')) {
                $('.xans-product-reviewpaging').remove();
             }
         } else if (oTarget == '#prdQnA') {
            var record = $('.xans-record-', '.xans-product-qna').first();
            if (record.length < 1 || record.is(':not(:visible)')) {
                $('.xans-product-qnapaging').remove();
            }
         }
     }
}

