// 쿠키 생성
var createCookie = function(name, value, days) {
	var expires;

	if (days) {
		var date = new Date();
		date.setTime(date.getTime() + (days * 24 * 60 * 60 * 1000));
		expires = "; expires=" + date.toGMTString();
	} else {
		expires = "";
	}
	document.cookie = escape(name) + "=" + escape(value) + expires + "; path=/";
}

// 쿠키 읽기
var readCookie = function(name) {
	var nameEQ = escape(name) + "=";
	var ca = document.cookie.split(';');
	for (var i = 0; i < ca.length; i++) {
		var c = ca[i];
		while (c.charAt(0) === ' ') c = c.substring(1, c.length);
		if (c.indexOf(nameEQ) === 0) return unescape(c.substring(nameEQ.length, c.length));
	}
	return null;
}

// 쿠키 삭제
var eraseCookie = function(name) {
	createCookie(name, "", -1);
}

// 할인율
var discountRate = function(t, name){
	if (!t) t = '';
	if (!name) name = '.discount_rate';
    
	$(t + ' ' + name).each(function(){
        
        // 판매가가 대체판매가 였을 경우 소비자가 제거 후 spec 영역 표시(숫자, 소수점 허용)
       if (!/^\d+(\.\d+)?$/.test($(this).data('prod-price'))) {
            $(this).closest('li').find('.product_custom').remove();
            $(this).closest('li').find('.spec .product_price').css('display','block');
            return;
        }
        
		var custom_price = parseFloat($(this).data('prod-custom').toString().replace(/[^0-9\.]/g,''));
        var prod_price = parseFloat($(this).data('prod-price'));
		var sale_price = parseFloat($(this).data('sale-price').toString().replace(/\([^)]*\)/g,'').replace(/[^0-9\.]/g,''));

        // 할인판매가가 있을 경우 판매가 대신 할인판매가를 대입
		if (sale_price > 0) {
			if (isNaN(custom_price)) custom_price = prod_price;
			prod_price = sale_price;
		}else{
        	$(this).closest('li').find('.spec .product_price').css('display','block');
        }

		let rate = 0
		if (!isNaN(custom_price)) rate = Math.round((custom_price - prod_price) / custom_price * 100);
		$(this).html(rate + '<span>%</span>');

        const targetSelector = sale_price > 0 ? '.spec li.prd_price_sale' : '.spec li.product_price';
        $(this).parent().find(targetSelector).prepend(this);

		(rate <= 0 || isNaN(rate)) ? $(this).hide() : $(this).show();
	});
}

// URL 파라미터 추출
var getParamUrl = function(sKey, url){
	if (!url) url = location.href;
	var sQueryString = url.substring(url.indexOf('?')+1);
	var aParam = {};

	if (sQueryString) {
		var aFields = sQueryString.split("&");
		var aField  = [];
		for (var i=0; i<aFields.length; i++) {
			aField = aFields[i].split('=');
			aParam[aField[0]] = aField[1];
		}
	}

	aParam.page = aParam.page ? aParam.page : 1;
	return sKey ? aParam[sKey] : aParam;
}
